sys_script_include.script:
var GetEmailAddress = Class.create();
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getEmail: function() {
		//Create GlideRecord of the sys_user table
		var userRecord = new GlideRecord("sys_user");
		//Use the this.getParameter() method to retrieve parameter values.
		//From the userID value received from client side, get me the record corresponding to this userID
		userRecord.get(this.getParameter('sysparam_userID'));
		return userRecord.email + '';
	},
    type: 'GetEmailAddress'
});
